import React, { useState, useEffect } from 'react';
import '../styles/components/Navbar.scss';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Sidebar from './Sidebar.js';

const NavbarPostlogin = () => {
    const [isOpen, setIsOpen] = useState(false); // State for sidebar
    const [cartCount, setCartCount] = useState(0);

    const toggleSidebar = () => {
        setIsOpen(!isOpen); // Toggle sidebar open/close state
    };

    useEffect(() => {
        const fetchCartItems = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) return;

                const response = await axios.get('http://localhost:5000/cart', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.status === 200 && response.data.productIds) {
                    setCartCount(response.data.productIds.length);
                }
            } catch (error) {
                console.error('Error fetching cart items:', error);
            }
        };

        fetchCartItems();
    }, []);

    return (
        <>
            <section className='navbarSection'>
                <ul>
                    <li className='logoname'>
                        <Link to="/swipenow">Repped</Link>
                    </li>
                    <button className='hamburger' onClick={toggleSidebar}>
                        &#9776;
                    </button>
                    <div className={`navLinks`}>
                        <li>
                            <button onClick={toggleSidebar}>Profile</button> {/* Toggle sidebar when clicked */}
                        </li>
                        <li>
                            <Link to="/newsletter_signup">Newsletter</Link>
                        </li>
                        <li>
                            <Link to="/cart">Cart {cartCount}</Link>
                        </li>
                    </div>
                </ul>
            </section>

            {/* Sidebar component */}
            <Sidebar isOpen={isOpen} toggleSidebar={toggleSidebar} />
        </>
    );
};

export default NavbarPostlogin;
