import React, { useState, useEffect } from 'react';
import TinderCard from 'react-tinder-card';
import Navbar from "../components/NavbarPostLogin.js";
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import "../styles/pages/Swipe.scss";
import axios from 'axios';

const Swipe = () => {
  const [characters, setCharacters] = useState([]);
  const [lastDirection, setLastDirection] = useState(null);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  // Fetch products from the backend API
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/products');
        setCharacters(response.data); 
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };


    fetchData();
  }, []);

  const getUserId = () => {
    const token = localStorage.getItem('token');
    if (token) {
        try {
            const decodedToken = jwtDecode(token);
            return decodedToken._id;
        } catch (error) {
            console.error('Error decoding token:', error);
            return null;
        }
    }
    return null;
};

const userId = getUserId();

  const swiped = async (direction, product) => {
    console.log('removing:', product.name);
    setLastDirection(direction);
    if (direction === 'up' && userId) {
        console.log('Adding to cart:', product);

        try {
            const response = await fetch('http://localhost:5000/api/cart/add-to-cart', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    userId,
                    productId: product.id
                })
            });

            const result = await response.json();

            if (result.success) {
                console.log('Product added to cart:', result.message);
                dispatch({ type: 'ADD_TO_CART', payload: product });
            } else {
                console.error('Error adding to cart:', result.message);
            }
        } catch (error) {
            console.error('Error adding to cart:', error.message);
        }
    }
};


  const outOfFrame = (name) => {
    console.log(`${name} left the screen!`);
  };

  return (
    <>
      <Navbar />
      <div className="dashboard">
        <div className="swiper-container">
          <div className="card-container">
            {characters && characters.length > 0 ? (
              characters.map((character) => (
                <TinderCard
                  className='swipe'
                  key={character._id}
                  onSwipe={(dir) => swiped(dir, character)}
                  onCardLeftScreen={() => outOfFrame(character.name)}
                >
                  <div className="card">
                    <div
                      style={{
                        backgroundImage: character.productURL ? `url(${character.productURL})` : 'none',
                        backgroundSize: 'cover',
                        backgroundPosition: 'center',
                        height: '400px',
                        width: '300px',
                      }}
                      className='card-image'
                    >
                      {character.productURL ? null : <p>No Image Available</p>}
                    </div>
                    <div className="card-details">
                      <h3 className='name'>{character.name}</h3>
                      <p>{character.description}</p>
                      <p>Price: ${character.price}</p>
                    </div>
                  </div>
                </TinderCard>
              ))
            ) : (
              <p>Loading products...</p>
            )}
          </div>
          <div className="swipe-info">
            {lastDirection ? (
              <p>You swiped {lastDirection}</p>
            ) : (
              <p />
            )}
          </div>
        </div>
      </div>
    </>
  );
};

export default Swipe;
