import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import TinderCard from 'react-tinder-card';
import Navbar from "../components/navbar";
import "../styles/pages/Swipe.scss";
import axios from 'axios';
const { default: jwt_decode } = require("jwt-decode");
const Swipe = () => {
  const [characters, setCharacters] = useState([]);
  const [lastDirection, setLastDirection] = useState(null);
  const [userId, setUserId] = useState(null);

  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
      const fetchData = async () => {
          try {
              const response = await fetch('http://localhost:5000/api/products');
              const data = await response.json();
              setCharacters(data);
          } catch (error) {
              console.error('Error fetching products:', error);
          }
      };

      const getUserId = () => {
          const token = localStorage.getItem('token');
          if (token) {
              try {
                  const decodedToken = jwt_decode(token);
                  return decodedToken._id; // Adjust the property name based on your token structure
              } catch (error) {
                  console.error('Error decoding token:', error);
                  return null;
              }
          }
          return null;
      };

      if (isAuthenticated) {
          const userId = getUserId();
          if (userId) {
              setUserId(userId);
              fetchData();
          } else {
              navigate('/');
          }
      }
  }, [isAuthenticated, navigate]);
  const swiped = async (direction, product) => {
    console.log('removing:', product.name);
    setLastDirection(direction);
    if (direction === 'up' && userId) {
        console.log('Adding to cart:', product);

        try {
            const response = await fetch('http://localhost:5000/api/cart/add-to-cart', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                },
                body: JSON.stringify({
                    userId,
                    productId: product.id
                })
            });

            const result = await response.json();

            if (result.success) {
                console.log('Product added to cart:', result.message);
                dispatch({ type: 'ADD_TO_CART', payload: product });
            } else {
                console.error('Error adding to cart:', result.message);
            }
        } catch (error) {
            console.error('Error adding to cart:', error.message);
        }
    }
};

  const outOfFrame = (name) => {
    console.log(`${name} left the screen!`);
  };

  return (
    <>
      <Navbar />
      <div className="dashboard">
        <div className="swiper-container">
          <div className="card-container">
            {characters.map((character) => (
              <TinderCard
                className='swipe'
                key={character._id}
                onSwipe={(dir) => swiped(dir, character)}
                onCardLeftScreen={() => outOfFrame(character.name)}
              >
                <div
                  style={{
                    backgroundImage: `url(${character.productURL})`,
                    backgroundSize: 'cover',
                    backgroundPosition: 'center',
                    height: '400px',
                    width: '300px',
                  }}
                  className='card'
                >
                  <h3 className='name'>{character.name}</h3>
                  <p>{character.description}</p>
                  <p>Price: ${character.price}</p>
                </div>
              </TinderCard>
            ))}
            <div className="swipe-info">
              {lastDirection ? (
                <p>You swiped {lastDirection}</p>
              ) : (
                <p />
              )}
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Swipe;
