import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/components/Sidebar.scss'; // Create this CSS file for styling

const Profile = ({ isOpen, onClose }) => {
    const [user, setUser] = useState({
        name: '',
        email: '',
        uname: ''
    });

    const [isEditing, setIsEditing] = useState({
        name: false,
        email: false,
        uname: false
    });

    useEffect(() => {
        // Fetch user data when the sidebar opens
        const fetchUserData = async () => {
            try {
                const token = localStorage.getItem('token');
                if (!token) return;

                const response = await axios.get('http://localhost:5000/profile', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                if (response.status === 200) {
                    setUser({
                        name: response.data.name,
                        email: response.data.email,
                        uname: response.data.uname
                    });
                }
            } catch (error) {
                console.error('Error fetching user data:', error);
            }
        };

        if (isOpen) {
            fetchUserData();
        }
    }, [isOpen]);

    const handleEdit = (field) => {
        setIsEditing((prev) => ({ ...prev, [field]: !prev[field] }));
    };

    const handleInputChange = (e) => {
        setUser({ ...user, [e.target.name]: e.target.value });
    };

    const handleSave = async () => {
        try {
            const token = localStorage.getItem('token');
            await axios.put(
                'http://localhost:5000/profile',
                { ...user },
                { headers: { 'Authorization': `Bearer ${token}` } }
            );
            setIsEditing({ name: false, email: false, uname: false });
        } catch (error) {
            console.error('Error saving profile data:', error);
        }
    };

    return (
        <div className={`sidebar ${isOpen ? 'open' : ''}`}>
            <div className="sidebar-header">
                <h2>Profile</h2>
                <button className="close-btn" onClick={onClose}>&times;</button>
            </div>

            <div className="profile-pic">
                <img src="/path/to/default-pic.png" alt="No Profile" />
            </div>

            <div className="profile-info">
                <div className="profile-field">
                    <label>Name: </label>
                    <input
                        type="text"
                        name="name"
                        value={user.name}
                        onChange={handleInputChange}
                        disabled={!isEditing.name}
                    />
                    <button onClick={() => handleEdit('name')}>
                        {isEditing.name ? 'Save' : 'Edit'}
                    </button>
                </div>

                <div className="profile-field">
                    <label>Username: </label>
                    <input
                        type="text"
                        name="uname"
                        value={user.uname}
                        onChange={handleInputChange}
                        disabled={!isEditing.uname}
                    />
                    <button onClick={() => handleEdit('uname')}>
                        {isEditing.uname ? 'Save' : 'Edit'}
                    </button>
                </div>

                <div className="profile-field">
                    <label>Email: </label>
                    <input
                        type="text"
                        name="email"
                        value={user.email}
                        onChange={handleInputChange}
                        disabled={!isEditing.email}
                    />
                    <button onClick={() => handleEdit('email')}>
                        {isEditing.email ? 'Save' : 'Edit'}
                    </button>
                </div>
            </div>

            <button className="save-btn" onClick={handleSave}>Save Changes</button>
        </div>
    );
};

export default Profile;
