import React, { useState, useEffect } from 'react';
import '../styles/components/Navbar.scss';
import { Link } from 'react-router-dom';
import axios from 'axios'; // Import axios for making API requests

const NavbarPostlogin = () => {
    const [isOpen, setIsOpen] = useState(false);
    const [cartCount, setCartCount] = useState(0);

    const toggleMenu = () => {
        setIsOpen(!isOpen);
    };

    useEffect(() => {
        const fetchCartItems = async () => {
            try {
                const token = localStorage.getItem('token'); // Retrieve the token from localStorage
                if (!token) return; // If no token is available, don't proceed

                // Make the API call to get the cart for the user
                const response = await axios.get('http://localhost:5000/cart', {
                    headers: {
                        'Authorization': `Bearer ${token}` // Send token in Authorization header
                    }
                });

                // If the request was successful, set the cartCount to the length of productIds array
                if (response.status === 200 && response.data.productIds) {
                    setCartCount(response.data.productIds.length); // Set the cart count
                }
            } catch (error) {
                console.error('Error fetching cart items:', error);
            }
        };

        fetchCartItems(); // Fetch cart items on component mount
    }, []); // Empty dependency array to run only once when the component mounts

    return (
        <section className='navbarSection'>
            <ul>
                <li className='logoname'>
                    <Link to="/swipenow">Repped</Link>
                </li>
                <button className='hamburger' onClick={toggleMenu}>
                    &#9776;
                </button>
                <div className={`navLinks ${isOpen ? 'open' : ''}`}>
                    <li>
                        <Link to="/onboarding">Profile</Link>
                    </li>
                    <li>
                        <Link to="/newsletter_signup">Newsletter</Link>
                    </li>
                    <li>
                        <Link to="/cart">Cart {cartCount}</Link> {/* Display cart count */}
                    </li>
                </div>
            </ul>
        </section>
    );
}

export default NavbarPostlogin;
